import React from "react";
import { Link } from "react-router-dom";
import { useEffect, useState } from "react";

const Home = () => {
  const [users, setUsers] = useState([]);
  useEffect(() => {
    getAllPosts();
  }, []);

  const getAllPosts = () => {
    const headers = { "Content-Type": "application/json" };
    fetch(`http://65.0.15.177:5000/user/getall`, { headers }, {method:'GET'})
      .then((response) => response.json())
      .then((json) => {
        setUsers(json.Users);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  return (
    <div className="container">
      <h1 className="text-center">Admin Panel</h1>
      <h6 className="text-danger">In case of data is not showing is it because of backend problem please download and enable cors extension into your chrome</h6>
      <div className="container">
        <Link className="btn btn-primary my-2" to={"/newuser"}>
          Add User
        </Link>
        <div className="row">
          {users.map((user, index) => {
            return (
              <div className="col-md-3 my-1" key={index}>
                <div className="card">
                  <div className="card-body">
                    <h5 className="card-title">{user.name}</h5>
                    <h6 className="card-subtitle mb-2 text-muted">
                      {user.phone_no}
                    </h6>

                    <a href="0" className="card-link">
                      Card link
                    </a>
                    <a href="0" className="card-link">
                      Another link
                    </a>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default Home;
