import React, { useState } from "react";
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";
import {AlertSuccess, AlertFailed} from '../state/action-creators/index'
const Alert = () => {
  const myState = useSelector((state) => state.AlertReducer);
  const Dispatch = useDispatch();
  return(
    <div className="">
      { myState ?
        <div className="alert alert-warning d-flex justify-content-between" role="alert" style={{width : "70vw"}}>
         <div> <strong>{myState}</strong> HIIII</div>
          <button type="button btn-warning border border-dark" class="btn" onClick={()=>{Dispatch(AlertFailed(false))}}>
            <span>X</span>
          </button>
        </div>: null
      }
    </div>
  );
};

export default Alert;
