import { put, takeEvery, call, all } from 'redux-saga/effects';
import {data, HideLoader, ShowLoader} from '../action-creators'
import {getAll, createUser} from './ApiCall';



export function* getAllUsers() {
    ShowLoader();
    const mydata =  yield call(getAll);
     yield put(data(mydata));
     HideLoader();
}

export function* createNewUser(){
  const status = yield call(createUser);
}
export function* watchApi() {
    yield takeEvery('getAllUsers', getAllUsers)
    yield takeEvery('createNewUser', createNewUser)
}
export default function* rootSaga() {
    yield all([
      watchApi()
    ])
  }
