import { put, takeEvery, call, all, takeLatest } from 'redux-saga/effects';
import { data } from '../action-creators'
import { getAll, createUser } from './ApiCall';
import {rootSaga} from './rootSaga';


export function* getAllUsers() {
  yield put({ type: 'show' })
  const mydata = yield call(getAll);
  //yield put({type: 'data', mydata});
  yield put(data(mydata));
  yield put({ type: 'hide' })
}

export function* createNewUser(payload) {
  const status = yield call(createUser(payload));
}
