import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import {APIKEY} from '../config/API';
const ServiceRecords = () => {
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const [service, setservice] = useState([]);
  const [car, setCar] = useState({});
 const Parameters ={
  method : "POST",
  body : JSON.stringify({
    "carid": 1,
    "servicing_date":"2022/01/02",
    "status": "finied"
  })
 }
  const ApiCall = () =>{
    fetch(`${APIKEY}/servicing/create`,)
      .then((response) => response.json())
      .then((json) => {
        setCar(json.Car);
        setservice(json.Car.Servicing);
      })
      .catch((err) => {
        console.log(err);
      });
  }
  useEffect(() => {
    fetch(`${APIKEY}/car/get/${params.get("id")}`,)
      .then((response) => response.json())
      .then((json) => {
        setCar(json.Car);
        setservice(json.Car.Servicing);
      })
      .catch((err) => {
        console.log(err);
      });
       // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <div className="container">
      <h2>Service Record </h2>
      <div>
        <p>
          Car Model : {car.model} <br />
          purchase Date : {car.purchase_date}
        </p>
      </div>
      <div className="row">
        {service.length <= 0 ? <h6>Service Record not available</h6> : service.map((car, index) => {
          return (
            <div className="col-md-3 my-1" key={index}>
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">Service Date : {car.servicing_date}</h5>
                  <h6 className="card-subtitle mb-2 text-muted">Status : {car.status}</h6>
                </div>
              </div>
            </div>
          );
        })}
        <div className="col-md-6 my-1">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">Service Date </h5>
                  <h6 className="card-subtitle mb-2 text-muted">Status</h6>
                </div>
                <div className="card-footer d-flex justify-content-between">
                  <button className="btn btn-primary" onClick={ApiCall("finished")}>Finished</button>
                  <button className="btn btn-primary" onClick={ApiCall("unfinished")}>Unfinished</button>
                  <button className="btn btn-primary" onClick={ApiCall("scheduled")}>Scheduled</button>
                </div>
              </div>
            </div>
      </div>
    </div>
  );
};

export default ServiceRecords;
