import { APIKEY } from '../../config/API';

import axios from "axios";

//get all user details
export const getAll = () => {
  return fetch(`${APIKEY}user/getall`)
    .then((response) => response.json())
    .then((json) => {
      
      return json;
    })
    .catch((err) => {
      console.log(err);
      
    });
}
//To create new user
export const createUser = (payload) =>{
 
  return axios.post(`${APIKEY}user/create`,payload,{headers: {"content-type": "application/json; charset=UTF-8",}})
    .then((response)=> response)
    .catch((err)=>{});
}
