import { APIKEY } from '../../config/API';
import { put} from 'redux-saga/effects';
import {AlertSuccess} from '../action-creators/index'
import axios from "axios";

//get all user details
export const getAll = () => {
  return fetch(`${APIKEY}user/getall`)
    .then((response) => response.json())
    .then((json) => {
      
      return json;
    })
    .catch((err) => {
      console.log(err);
      
    });
}
//To create new user
export const createUser = (payload) =>{
 
  axios.post(`${APIKEY}user/create`,payload,{headers: {"content-type": "application/json; charset=UTF-8",}})
    .then((response)=>{
      if(response.data.error === 0){put(AlertSuccess("New User Addedd"));}
      else{AlertSuccess("Duplicate entries Not Allowted");}
    })
    .catch((err)=>{put(AlertSuccess(err));});
}
