import React from "react";
import { Link } from "react-router-dom";
import { useEffect, useState } from "react";

const Home = () => {
  const [users, setUsers] = useState([]);
  const [loader, setLoader] = useState(false);
  useEffect(() => {
    getAllPosts();
     // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const getAllPosts = () => {
    setLoader(true);
    fetch(`http://65.0.15.177:5000/user/getall`)
      .then((response) => response.json())
      .then((json) => {
        setUsers(json.Users);
        setLoader(false);
      })
      .catch((err) => {
        console.log(err);
        setLoader(false);
      });
  };
  return (
    <div className="container">
      <h1 className="text-center">Admin Panel</h1>
      <h6 className="text-danger">
        In case of data is not showing is it because of backend problem please
        download and enable Moesif CORS extension into your chrome to fetch data from backend.
      </h6>
      <p>Many of the api routes are not working so i only fetched data from working api. for more details contact 8830644265</p>
      {loader ? (
        
        <div className="text-center mt-5">
          <div class="spinner-border text-primary " role="status">
            <span class="visually-hidden">Loading...</span>
          </div>
        </div>
      ) : (
        <div className="container">
          <Link className="btn btn-primary my-2" to={"/newuser"}>
            Add User
          </Link>
          <div className="row">
            {users.map((user, index) => {
              return (
                <div className="col-md-3 my-1" key={index}>
                  <div className="card">
                    <div className="card-body">
                      <h5 className="card-title"> User Name : {user.name}</h5>
                      <h6 className="card-subtitle mb-2 text-muted">
                        Phone : {user.phone_no}
                      </h6>

                      <Link
                        to={`/cardetails?user=${user.id}`}
                        className="card-link btn btn-primary"
                      >
                        Cars Details
                      </Link>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
