import React, { useEffect, useState } from "react";
import { Link, useLocation } from "react-router-dom";

const CarDetails = () => {
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const [cars, setcars] = useState([]);

  useEffect(() => {
    fetch(
      `http://65.0.15.177:5000/user/get/${params.get(
        "user"
      )}`
    )
      .then((response) => response.json())
      .then((json) => {
        setcars(json.User.Cars);
      })
      .catch((err) => {
        console.log(err);
      });
       // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <div className="container">
      <h2>List of cars </h2>
      <div className="row">
        {cars.length <= 0? <h6 className="mt-4 text-danger">No data found</h6>: cars.map((car, index) => {
          return (
            <div className="col-md-3 my-1" key={index}>
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">{car.model}</h5>
                  <h6 className="card-subtitle mb-2 text-muted">
                    {car.purchase_date}
                  </h6>
                  <Link to={`/service?id=${car.id}`} className="btn btn-primary"> Service Record</Link>
                </div>
              </div>
            </div>
          );
        })}

      </div>
    </div>
  );
};

export default CarDetails;
