import { put, takeEvery, call, all } from 'redux-saga/effects';
import { APIKEY } from './config/API';
import {data} from './state/action-creators/'
export function* helloSaga() {
    console.log('Hello Sagas!')
}

function getData(){
    return fetch(`${APIKEY}user/getall`)
    .then((response) => response.json())
    .then((json) => json)
    .catch((err) => {
      console.log(err);
    });
}
export function* fetchApii() {
    const mydata =  yield call(getData);
     yield put(data(mydata))
}

export function* watchApi() {
    yield takeEvery('fetchApi', fetchApii)
}
export default function* rootSaga() {
    yield all([
      helloSaga(),
      watchApi()
    ])
  }
