import { APIKEY } from '../../config/API';

export const getAll = () => {

  ShowLoader()
  return fetch(`${APIKEY}user/getall`)
    .then((response) => response.json())
    .then((json) => {
      return json;
      HideLoader();
    })
    .catch((err) => {
      console.log(err);
      ShowLoader();
    });
}
