import { put, takeEvery, call, all, takeLatest, delay } from 'redux-saga/effects';
import { data } from '../action-creators'
import { getAll, createUser } from './ApiCall';



export function* getAllUsers() {
  yield put({ type: 'show' })
  const mydata = yield call(getAll);
  yield put(data(mydata));
  yield put({ type: 'hide' })
}

export function* createNewUser(payload) {
  yield put({ type: 'show' })
  try { 
    const status = yield call(createUser,payload.message);
    if (status.data.error === 0) {
      delay(500);
      console.log(status.data)
    } 
  } catch (error) {
    console.log(error)
  }
  yield put({ type: 'hide' })
  
}
export function* watchApi() {
  yield takeEvery('getAllUsers', getAllUsers)
  yield takeLatest('createNewUser', createNewUser)
}
export default function* rootSaga() {
  yield all([
    watchApi()
  ])
}
