import { APIKEY } from '../../config/API';
import {} from '../action-creators/index'
import axios from "axios";
export const getAll = () => {
  return fetch(`${APIKEY}user/getall`)
    .then((response) => response.json())
    .then((json) => {
      
      return json;
    })
    .catch((err) => {
      console.log(err);
      
    });
}
export const createUser = (payload) =>{
  axios.post(`${APIKEY}user/create`,payload,{headers: {"content-type": "application/json; charset=UTF-8",}})
    .then((response)=>{
      if(response.data.error === 0){navigate("/");Dispatch(AlertSuccess("New User Addedd"));}
      else{Dispatch(AlertSuccess("Duplicate entries Not Allowted"));}
    })
    .catch((err)=>{Dispatch(AlertSuccess(err));});
}
