import { put, takeEvery, call, all, takeLatest } from 'redux-saga/effects';
function* watchApi() {
    yield takeEvery('getAllUsers', getAllUsers)
    yield takeLatest('createNewUser', createNewUser)
  }
function* rootSaga() {
    yield all([
      watchApi()
    ])
  }
export default rootSaga;
