import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import {APIKEY} from '../config/API';
const ServiceRecords = () => {
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const [service, setservice] = useState([]);
  const [car, setCar] = useState({});
  const body = {userid : params.get("id")}
  useEffect(() => {
    fetch(`${APIKEY}/car/getall`,{
     
      // Adding method type
      method: "GET",
       
      // Adding body or contents to send
      body: JSON.stringify({
          
          userid: 1
      }),
       
      // Adding headers to the request
      headers: {
          "Content-type": "application/json; charset=UTF-8"
      }
  })
      .then((response) => response.json())
      .then((json) => {
        setCar(json.Car);
        setservice(json.Car.Servicing);
      })
      .catch((err) => {
        console.log(err);
      });
       // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <div className="container">
      <h2>Service Record </h2>
      <div>
        <p>
          Car Model : {car.model} <br />
          purchase Date : {car.purchase_date}
        </p>
      </div>
      <div className="row">
        {service.length <= 0 ? <h6>Service Record not available</h6> : service.map((car, index) => {
          return (
            <div className="col-md-3 my-1" key={index}>
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">Service Date : {car.servicing_date}</h5>
                  <h6 className="card-subtitle mb-2 text-muted">Status : {car.status}</h6>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default ServiceRecords;
